function init_send_msg(a){$("#send-msg").click(function(b){show_send_box(b.pageX,b.pageY,a)});$(".email").click(function(b){show_send_box(b.pageX,b.pageY,$(b.target).attr("data-name"))})}function show_send_box(a,d,c){if($("body").find("#send-msg-form").length>0){return}var b=$("<div id='send-msg-form'style='position:absolute;'><div id='send-msg-box' class='confirm-box'><h1>\u53d1\u9001\u7ed9<span class='username'>"+c+"</span></h1><textarea id='content'class='text' style='height:50px;margin-bottom: 10px;'></textarea><p><a id='send' class='button green' href='javascript:;'>\u53d1\u9001</a><a id='cancel' class='button orange' href='javascript:;'>\u53d6\u6d88</a></p></div></div>");b.css({top:d,left:a});b.find("#cancel").click(function(){b.remove()});b.find("#send").click(function(){var e=b.find("#content").val();if(e.length<=0){alert("\u7ad9\u5185\u4fe1\u5185\u5bb9\u4e0d\u80fd\u4e3a\u7a7a");return}q_get("msg/send",{u:c,c:e},function(f){},function(){alert("\u53d1\u9001\u5931\u8d25")});b.remove()});$("body").append(b)};
